import styled from "styled-components";

export const HomeContentContainer = styled.div`
  width: 100%;
  display: flex;
  flex-direction: column;
  /* margin: 5%; */
  padding: 25px;

  .title-container {
    width: 50%;
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: flex-start;
    margin-left: 170px;
    margin-top: 4%;

    h1 {
      display: flex;
      font-weight: bold;
      font-size: 35px;
      margin-bottom: 1%;
    }

    h2 {
      display: flex;
      font-weight: lighter;
      font-size: 17px;
    }
  }

  .home-content {
    display: flex;
    flex-direction: row;
    height: 55%;
    justify-content: center;
    width: 100%;

    .text-content {
      display: flex;
      flex-direction: column;
      justify-content: center;
      align-items: flex-start;
      height: 50%;
      margin-left: 5%;
      margin-top: 5%;

      h2 {
        font-size: 25px;
        display: flex;
        font-weight: lighter;
        justify-content: right;
        font-size: 23px;
        margin-bottom: 8%;
        font-weight: lighter;
      }

      li {
        margin-left: 25%;
      }
    }
  }
`;
