import styled from "styled-components";

export const ProdutoContainer = styled.div`
  display: flex;
  flex-direction: row;
  align-items: center;
  width: 100%;
  height: 100%;
  margin: 2%;
`;

export const ProdFormContainer = styled.div`
  display: flex;
  flex-direction: column;
  background-color: #2b2929;
  width: 60%;
  height: 100%;
`;
