import styled from "styled-components";

export const SideBarContainer = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  background-color: black;
  height: 100vh;
  width: 7%;
  border-top-right-radius: 15px;
  border-bottom-right-radius: 15px;
  /* padding: 20px; */

  .home-icon {
    margin-bottom: 50%;
    padding: 24px;
  }

  .option-icon {
    margin-bottom: 25%;
    padding: 10px;
    width: 90%;
    height: 10%;
    background-color: #7935c7;
    border-radius: 25px;
    :hover {
      background-color: #7935c7;
    }
  }
`;
