import styled from "styled-components";

export const HomeContentContainer = styled.div`
  /* background-color: red; */
  width: 100%;
  display: flex;
  flex-direction: column;
  /* align-items: flex-end; */
  margin-right: 7%;
  margin-top: 2%;
  padding: 25px;

  .title-container {
    width: 90%;

    h1 {
      display: flex;
      font-weight: bold;
      font-size: 35px;
      margin-bottom: 1%;
      justify-content: right;
    }

    h2 {
      display: flex;
      font-weight: lighter;
      justify-content: right;
      font-size: 20px;
    }
  }

  .home-content {
    display: flex;
    flex-direction: row;
    height: 70%;
    justify-content: center;
    width: 100%;
    margin-top: 2%;

    .text-content {
      display: flex;
      flex-direction: column;
      justify-content: right;
      height: 50%;
      margin-left: 5%;
      margin-top: 5%;
      h2 {
        font-size: 25px;

        display: flex;
        font-weight: lighter;
        justify-content: right;
        font-size: 20px;
      }
    }
  }
`;
