import styled from "styled-components";

export const ProdutoContainer = styled.div`
  display: flex;
  flex-direction: row;
  justify-content: center;
  width: 100%;
  height: 90vh;
  margin: 2%;
`;

export const ProdFormContainer = styled.div`
  display: flex;
  flex-direction: column;
  justify-content: left;
  align-items: left;
  background-color: #2b2929;
  width: 40%;
  border-radius: 15px;

  .title {
    display: flex;
    align-items: center;
    justify-content: center;
    padding: 20px;
    padding-bottom: 0px;
    font-size: 28px;
    color: #6b5def;
    margin-top: 2%;
    font-weight: bold;
  }

  .form {
    padding: 40px;
    font-size: 10px;

    .input-preco {
      display: flex;
      flex-direction: row;
      align-items: center;
      justify-content: flex-end;
      background-color: red;
      width: 100%;

      input {
          display: flex;
          justify-content: left;
      align-items: left;

          width: 20%;
      }

      h1 {
          background-color: white;
          font-size: 20px;
          width: 100%;
      }
    }

    input {
      width: 100%;
      padding: 10px;
      background-color: #2b2929;
      border: none;
      font-size: 18px;
    }

    hr {
      margin-top: 0%;
      margin-bottom: 4%;
      border-color: #573185;
    }
  }
`;
