import styled from "styled-components";

export const SideBarContainer = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  background-color: #2B2929;
  height: 100vh;
  width: 7%;
  border-top-right-radius: 15px;
  border-bottom-right-radius: 15px;

  .teste { 
    height: 100%;
    width: 100%;
    background-color: white;
  }
  .home-icon {
    margin-bottom: 50%;
    padding: 24px;
    height: 15%;
  }

  .option-icon {
    margin-bottom: 25%;
    padding: 10px;
    width: 80%;
    height: 9%;
    border-radius: 25px;
    opacity: 1;
    transition: .5s ease;
   

    :hover {
      background-color: white;
    }
  }
`;
