import styled from "styled-components";

export const HomeContentContainer = styled.div`
  /* background-color: red; */
  width: 100%;
  display: flex;
  flex-direction: column;
  /* align-items: flex-end; */
  margin-right: 7%;
  margin-top: 2%;
  padding: 25px;


  h1 {
    display: flex;

    font-weight: bold;
    font-size: 35px;
    margin-bottom: 1%;
    justify-content: right;

  }

  h2 {
    display: flex;
    font-weight: lighter;
    justify-content: right;
    font-size: 20px;
  }

  .home-content {
      display: flex;
      flex-direction: row;
      background-color: red;
  }
`;
