import axios from "axios";
import React, { useState } from "react";
import { CliFormContainer, ClienteContainer } from "./Cliente.Style";

export const Cliente = () => {
  //  const [cep, useState]  = useState('');
  const [cep, setCep] = useState("");
  const [rua, setRua] = useState("");


  

  const handleSubmit = () => {
    

    axios.get(`https://viacep.com.br/ws/${cep}/json`).then((res) => {
      console.log(JSON.parse(res));
        setRua(res.data.logradouro);
    
      alert(rua);
    });
  };
  const handleChangeCEP = (event) => {
    console.log(event.target.value);
    setCep(event.target.value);
  };

  return (
    <ClienteContainer>
      <CliFormContainer>
        <div className="title">Cadastro de Cliente</div>

        <form className="form">
          <div className="blocos">
            <div className="bloco-esquerda">
              <input placeholder="Código"></input>
              <hr></hr>
              <input placeholder="Nome"></input>
              <hr></hr>
              <input placeholder="E-mail"></input>
              <hr></hr>
              <input placeholder="Telefone"></input>
              <hr></hr>
              <div className="input-cliente">
                <input type="checkbox"></input>
                <h1>Cliente ativo?</h1>
              </div>
            </div>
            <div className="espacamento-inputs"></div>
            <div className="bloco-direita">
              <input
                placeholder="CEP"
                onChange={handleChangeCEP}
                value={cep}
              ></input>
              <hr></hr>
              <input placeholder="Endereco"></input>
              <hr></hr>
              <input placeholder="Número"></input>
              <hr></hr>
              <input placeholder="Cidade"></input>
              <hr></hr>
              <input placeholder="Estado"></input>
              <hr></hr>
            </div>
          </div>
          <div className="btn-cadastrar">
            <button onClick={handleSubmit} type="submit">
              Cadastar
            </button>
          </div>
        </form>
      </CliFormContainer>
    </ClienteContainer>
  );
};
