import styled from "styled-components";

export const ClienteContainer = styled.div`
  display: flex;
  flex-direction: row;
  justify-content: center;
  width: 100%;
  height: 90vh;
  margin: 2%;
`;

export const CliFormContainer = styled.div`
  display: flex;
  flex-direction: column;
  justify-content: left;
  align-items: left;
  background-color: #2b2929;
  width: 60%;
  border-radius: 10px;
  box-shadow: 0 0 1em #352d43;

  .title {
    display: flex;
    align-items: center;
    justify-content: center;
    padding: 20px;
    padding-bottom: 0px;
    font-size: 28px;
    color: #6b5def;
    margin-top: 2%;
    font-weight: bold;
  }

  .input-container {
    display: flex;
    flex-direction: row;
    justify-content: space-between;
    width: 100%;
    height: 65%;
    background-color: white;
    
    .bloco-direita {
      display: flex;
      flex-direction: column;
      background-color: red;
      width: 50%;
      height: 70%;
    }

    .bloco-esquerda {
      display: flex;
      flex-direction: column;
      background-color: blue;
      width: 50%;
      height: 70%;
    }
  }

  
`;
