import styled from "styled-components";

export const HomeContentContainer = styled.div`
  width: 100%;
  display: flex;
  flex-direction: column;
  margin-right: 7%;
  margin-top: 2%;
  padding: 25px;

  .title-container {
    width: 100%;
    display: flex;
    flex-direction: column;
    justify-content: left;

    h1 {
      display: flex;
      font-weight: bold;
      font-size: 35px;
      margin-bottom: 1%;
     
    }

    h2 {
      display: flex;
      font-weight: lighter;
    
      font-size: 17px;
    }
  }

  .home-content {
    display: flex;
    flex-direction: row;
    height: 70%;
    justify-content: center;
    width: 100%;
    margin-top: 0%;

    .text-content {
      display: flex;
      flex-direction: column;
      justify-content: center;
      align-items: flex-start;
      height: 50%;
      margin-left: 5%;
      margin-top: 5%;

      h2 {
        font-size: 25px;
        display: flex;
        font-weight: lighter;
        justify-content: right;
        font-size: 23px;
        margin-bottom: 8%;
        font-weight: lighter;
      }

      li {
        margin-left: 25%;
      }
    }
  }
`;
