import styled from "styled-components";

export const ProdutoContainer = styled.div`
  display: flex;
  flex-direction: row;
  justify-content: center;
  width: 100%;
  height: 90vh;
  margin: 2%;
`;

export const ProdFormContainer = styled.div`
  display: flex;
  flex-direction: column;
  background-color: #2b2929;
  width: 40%;
  border-radius: 15px;

  .title {
    display: flex;
    align-items: center;
    justify-content: center;
    padding: 20px;
    font-size: 28px;
    color: #6B5DEF;
    margin-top: 2%;
    font-weight: bold;
  }

  .form {
    padding: 40px;

    .input-preco {
      display: flex;
      align-items: right;
      justify-content: right;
      background-color: red;
    }

    input {
      width: 100%;
      padding: 10px;
      background-color: #2b2929;
      border: none;
      font-size: 18px;
    }

    hr {
      margin-top: 0%;
      margin-bottom: 4%;
      border-color: #573185;
    }
  }
`;
