import styled from "styled-components";

export const HomeContentContainer = styled.div`
  width: 100%;
  display: flex;
  flex-direction: column;
  /* justify-content: flex-end; */
  margin-top: 1%;
  padding: 25px;

  .title-container {
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;
    background-color: red;

   

/*  */

    h1 {
      display: flex;
      font-weight: bold;
      font-size: 35px;
      margin-bottom: 0%;
    }

    h2 {
      display: flex;
      font-weight: lighter;
      font-size: 17px;
    }
  }

  .home-content {
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;
   
   
    background-color: blue;

    .home-image {
      width: 100%;
      height: 50%;
      background-color: white;
    }

    .text-content {
      display: flex;
      flex-direction: column;
      justify-content: center;
      align-items: flex-start;
      height: 50%;
      
     
     

      h2 {
        font-size: 25px;
        display: flex;
        font-weight: lighter;
        justify-content: right;
        font-size: 23px;
        margin-bottom: 8%;
        font-weight: lighter;
      }

      li {
        margin-left: 25%;
      }
    }
  }
`;
